export default {
  data: () => ({
      valid: true,
      company: '',
      vehicle_type:'',
      phone:'',
      driver_f_name:'',
      driver_l_name:'',
      address:'',
      county:'',
      country:'',
      requiredRules: [v => !!v || 'This input is required'],

    }),

    methods: {
      add_supplier() {

        var data = { company: this.company, vehicle: this.vehicle_type, phone: this.phone, driver_f_name:this.driver_f_name, driver_l_name:this.driver_l_name, address:this.address, county:this.county, country:this.country }

        var requestOptions = {
          method: "POST", // *GET, POST, PUT, DELETE, etc.
          mode: "cors", // no-cors, cors, *same-origin
          cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
          credentials: "same-origin", // include, *same-origin, omit
          headers: {
            Accept: "application/json",
          },
          redirect: "follow", // manual, *follow, error
          referrer: "no-referrer", // no-referrer, *client
          body: JSON.stringify(data) // body data type must match "Content-Type" header
        };

        fetch(process.env.VUE_APP_STAGING_ENDPOINT + "create-supplier", requestOptions)
          .then(response => response.json())
          .then(r => {
            if (r.status == '200') {

              this.$store.commit('supplier')
              this.$router.push('/supplier')
              this.$refs.form.reset()

            } else {
              alert("Error, Kindly contact developer")
              this.$router.push('/supplier')
              this.$store.commit('supplier')
            }

            })
          .catch(error => console.log('error', error));

      }
    }

}
